<script>
export default {
  data () {
    return {
      oliviaImg: [
        'olivia_profil',
        '',
        'olivia_profil_2'
      ],
      janaImg: [
        'jana_profil',
        '',
        'jana_profil_2'
      ],
      i: 1,
      y: 8000,
      firstTime: true
    }
  },
  methods: {
    changeImages () {
      let x = setInterval(() => {
        if (this.firstTime) {
          this.oliviaImg[1] = 'olivia_profil_1'
          this.janaImg[1] = 'jana_profil_1'
        }
        this.y = 2000
        if (this.i < 2) {
          this.i = this.i + 1
        } else {
          clearInterval(x)
          this.i = 0
          this.changeImages()
        }
        this.firstTime = false
      }, this.y)
    }
  },
  beforeMount () {
    this.changeImages()
  }
}
</script>

